mapper.update:
update
    alarm_info
set
    alarm_span = alarm_span + 1,
    push_level = #{pushLevel}
where
    com_port = #{comPort}
and
    point_name = #{pointName}
LIMIT 1;

update
    alarm_info
set
    alarming = 1,
    alarm_time = #{alarmTime}
where
    com_port = #{comPort}
and
    point_name = #{pointName}
LIMIT 1;

mapper.select:
select
    id
from
    alarm_info
where
    com_port = #{comPort}
and
    point_name = #{pointName}
limit 1;
